import React, { useState, useEffect } from 'react'
import SignIn from './SignIn'
import SignUp from './SignUp'
import '../css/header.css'
import SearchBar from './SearchBar'
import '../css/profile.css'
import '../css/language.css'
import '../css/logo.css'
import LanguageSelector from './Language'
import NavigationButtons from './NavButton'
import SearchIcon from './SearchIcon'

function Header() {
	const [backgroundColor, setBackgroundColor] = useState('transparent')
	const [isSignInWindowOpen, setIsSignInWindowOpen] = useState(false)
	const [isSignUpWindowOpen, setIsSignUpWindowOpen] = useState(false)

	const handleScroll = () => {
		const scrollTop = window.scrollY || document.documentElement.scrollTop
		if (scrollTop > 50) {
			setBackgroundColor('#121212')
		} else {
			setBackgroundColor('transparent')
		}
	}

	const handleSignInWindOpen = () => {
		setIsSignInWindowOpen(true)
		setIsSignUpWindowOpen(false)
	}

	const handleSignUpWindOpen = () => {
		setIsSignUpWindowOpen(true)
		setIsSignInWindowOpen(false)
	}

	useEffect(() => {
		window.addEventListener('scroll', handleScroll)

		return () => {
			window.removeEventListener('scroll', handleScroll)
		}
	}, [])

	return (
		<header style={{ backgroundColor }}>
			<a className='logo__link' href='index.html'>
				<div className='logo'>GGMORE</div>
			</a>
			<NavigationButtons />
			<div className='search__cont'>
				<div className='search'>
					<SearchBar />
				</div>
				<SearchIcon />
			</div>
			<LanguageSelector />
			<nav>
				<button onClick={handleSignInWindOpen}>Sign In</button>
				<button onClick={handleSignUpWindOpen}>Sign Up</button>
			</nav>
			{isSignInWindowOpen && <SignIn />}
			{isSignUpWindowOpen && <SignUp />}
		</header>
	)
}

export default Header
