import React, { useState } from 'react'
import '../css/slidertest.css' // Подключите стили для слайдера

function SliderTest() {
	const slides = [
		'../img/slide__first.png',
		'../img/slide__second.png',
		'../img/slide__third.png',
		'../img/slide__fourth.png',
	]
	const [nowSlide, setSlide] = useState(0)
	const nextSlide = () => {
		let newSlide = nowSlide + 1

		if (newSlide >= slides.lenght) {
			newSlide = 0
		}

		setSlide(newSlide);
	};

  const prevSlide = () => {
    let newSlide = nowSlide - 1;

