import React, { useState } from 'react'
import '../css/slidertest.css'

function SliderTest() {
	const slides = [
    '/'
	]
	const [nowSlide, setSlide] = useState(0)
	const nextSlide = () => {
		let newSlide = nowSlide + 1

		if (newSlide >= slides.lenght) {
			newSlide = 0
		}

		setSlide(newSlide);
	};

  const prevSlide = () => {
    let newSlide = nowSlide - 1;

    if (newSlide < 0) {
      newSlide = slides.length - 1; 
    };

    setSlide(newSlide);
  };
	return (
		<div className='slidertest'>
			<img src={slides[nowSlide]} alt='imgtest' className='imgtest' />
			<button className='slider__button' onClick={prevSlide}></button>
			<button className='slider__button' onClick={nextSlide}></button>
		</div>
	)
}

export default SliderTest
