import React from 'react'
import Header from './components/Header'
import Slider from './components/Slider'
import WhyChooseUs from './components/WhyChooseUs'
import Footer from './components/Footer'
import FormModalContainer from './components/FormModalContainer'

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			isSignInWindowOpen: false,
			isSignUpWindowOpen: false,
		}
	}

	handleSignInWindOpen = () => {
		console.log('Sign In clicked')
		this.setState({
			isSignInWindowOpen: true,
			isSignUpWindowOpen: false,
		})
	}

	handleSignUpWindOpen = () => {
		console.log('Sign Up clicked')
		this.setState({
			isSignUpWindowOpen: true,
			isSignInWindowOpen: false,
		})
	}

	render() {
		return (
			<div className='container'>
				<Header
					onSignInClick={this.handleSignInWindOpen}
					onSignUpClick={this.handleSignUpWindOpen}
				/>
				<Slider />
				<FormModalContainer
					isSignInWindowOpen={this.state.isSignInWindowOpen}
					isSignUpWindowOpen={this.state.isSignUpWindowOpen}
				/>
				<WhyChooseUs />
				<Footer />
			</div>
		)
	}
}

export default App
