import React, { useState } from 'react'
import Icon from './IconProfile'
import SignIn from './SignIn'
import SignUp from './SignUp'

function FormModalContainer() {
	const [isSignInWindowOpen, setIsSignInWindowOpen] = useState(false)
	const [isSignUpWindowOpen, setIsSignUpWindowOpen] = useState(false)

	const handleSignInWindOpen = () => {
		console.log('Sign In clicked')
		setIsSignInWindowOpen(true)
		setIsSignUpWindowOpen(false)
	}

	const handleSignUpWindOpen = () => {
		console.log('Sign Up clicked')
		setIsSignUpWindowOpen(true)
		setIsSignInWindowOpen(false)
	}
	console.log('Passing to Icon:', handleSignInWindOpen, handleSignUpWindOpen) // Проверим, что пропсы передаются

	return (
		<div>
			<Icon
				onSignInClick={handleSignInWindOpen}
				onSignUpClick={handleSignUpWindOpen}
			/>

			{isSignInWindowOpen && <SignIn />}
			{isSignUpWindowOpen && <SignUp />}
		</div>
	)
}

export default FormModalContainer
