import React, { useState } from 'react'
import icon from '../img/iconprofile.svg'

const Icon = ({ onSignInClick, onSignUpClick }) => {
	const [showProfileMenu, setShowProfileMenu] = useState(false)
	const [timeoutId, setTimeoutId] = useState(null)
	
	const handleProfileMouseEnter = () => {
		if (timeoutId) {
			clearTimeout(timeoutId)
		}
		setShowProfileMenu(true)
	}

	const handleProfileMouseLeave = () => {
		const newTimeoutId = setTimeout(() => {
			setShowProfileMenu(false)
		}, 2000)
		setTimeoutId(newTimeoutId)
	}

	const handleMenuMouseEnter = () => {
		if (timeoutId) {
			clearTimeout(timeoutId)
		}
		setShowProfileMenu(true)
	}

	const handleMenuMouseLeave = () => {
		const newTimeoutId = setTimeout(() => {
			setShowProfileMenu(false)
		}, 400)
		setTimeoutId(newTimeoutId)
	}

	const handleSignInClick = () => {
		console.log('Sign In button clicked') // Проверка, что кнопка нажимается
		if (onSignInClick) onSignInClick() // Вызываем функцию из пропсов
	}

	const handleSignUpClick = () => {
		console.log('Sign Up button clicked') // Проверка, что кнопка нажимается
		if (onSignUpClick) onSignUpClick() // Вызываем функцию из пропсов
	}

	return (
		<div
			className='profile'
			onMouseEnter={handleProfileMouseEnter}
			onMouseLeave={handleProfileMouseLeave}
			onClick={() => {
				if (onSignInClick) onSignInClick()
				setShowProfileMenu(true)
			}}
		>
			<a href='#'>
				<img src={icon} alt='Profile' />
			</a>
			<div
				className={`profile-menu ${showProfileMenu ? 'show' : ''}`}
				onMouseEnter={handleMenuMouseEnter}
				onMouseLeave={handleMenuMouseLeave}
			>
				<ul>
					<li>
						<button onClick={onSignInClick}>Sign In</button>
					</li>
					<li>
						<button onClick={onSignUpClick}>Sign Up</button>
					</li>
				</ul>
			</div>
		</div>
	)
}

export default Icon
