import React, { useState, useEffect } from 'react'
import '../css/whychooseus.css'

function WhyChooseUs() {
	const [backgroundColor, setBackgroundColor] = useState(
		'rgba(255, 255, 255, 0)'
	) // Начальный прозрачный фон

	const handleScroll = () => {
		if (window.scrollY > 15) {
			setBackgroundColor('rgba(255, 255, 255, 1)') // Цветной фон
		} else {
			setBackgroundColor('rgba(255, 255, 255, 0)') // Прозрачный фон
		}
	}

	useEffect(() => {
		window.addEventListener('scroll', handleScroll)

		// Удаляем слушатель при размонтировании компонента
		return () => {
			window.removeEventListener('scroll', handleScroll)
		}
	}, [])
	return (
		<section class='why_our_site'>
			<h2>Why You Need GGMore?</h2>
			<p>
				GGMore is your ultimate destination for all things gaming. We provide
				in-depth articles, comprehensive game guides, and the latest news to
				keep you updated with your favorite games. Our platform is designed for
				gamers who want to stay ahead of the curve.
			</p>
			<ul>
				<li>Discover new games and strategies</li>
				<li>Access the latest updates and news</li>
				<li>Enhance your gaming experience with expert guides</li>
				<li>Join a community of passionate gamers</li>
			</ul>
			<p>
				Stay informed, stay ahead, and make the most of your gaming journey with
				GGMore. Join us now and take your gaming experience to the next level!
			</p>
		</section>
	)
}

export default WhyChooseUs
