import React, { useState } from 'react'
import '../css/slidertest.css' // Подключите стили для слайдера


function SliderTest() {
	const slides = [
		'https://via.placeholder.com/600x300?text=Slide+1',
		'https://via.placeholder.com/600x300?text=Slide+2',
		'https://via.placeholder.com/600x300?text=Slide+3',
	]

	const [currentSlide, setCurrentSlide] = useState(0)

const nextSlide = () => {
	console.log('Next slide clicked')
	setCurrentSlide(prevSlide => (prevSlide + 1) % slides.length)
}

	const prevSlide = () => {
		setCurrentSlide(prevSlide =>
			prevSlide === 0 ? slides.length - 1 : prevSlide - 1
		)
	}

	return (
		<div className='slidertest'>
			<button onClick={prevSlide} className='slider_button'>
				Prev
			</button>
			<img className='imgtest' src={slides[currentSlide]} alt={`Slide ${currentSlide + 1}`} />
			<button onClick={nextSlide} className='slider_button'>
				Next
			</button>
		</div>
	)
}

export default SliderTest
