import React, { useState } from 'react'
import '../css/slidertest.css'

function SliderTest() {
	const slides = [
		'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTx0UYVnugCBW4Rjvej_OVqEWs-eUlFkr1Fgg&s',
		'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQVSs3kyGOCmws-mglvjCUZzfNZegtXCMWhXzJlQMRJGtpef6a188WdYZYSgpMQ2e9R87U&usqp=CAU',
		'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRS_ewi-JPDjbRhuQ9WlaAmwCcDDBacLYnz5BLNZNbWnKt5Uf5ndck6qkigcWlDLSVXxi0&usqp=CAU',
		'https://example.com/image4.jpg',
	]
	const [nowSlide, setSlide] = useState(0)

	const nextSlide = () => {
		let newSlide = nowSlide + 1
		if (newSlide >= slides.length) {
			newSlide = 0
		}
		setSlide(newSlide)
	}

	const prevSlide = () => {
		let newSlide = nowSlide - 1
		if (newSlide < 0) {
			newSlide = slides.length - 1
		}
		setSlide(newSlide)
	}

	return (
		<div className='slidertest'>
			<img src={slides[nowSlide]} alt='Slide' className='imgtest' />
			<button className='slider__button' onClick={prevSlide}>
				Prev
			</button>
			<button className='slider__button' onClick={nextSlide}>
				Next
			</button>
		</div>
	)
}

export default SliderTest
