import React from 'react'
import icon from '../img/iconprofile.svg'

class Icon extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			showProfileMenu: false,
			timeoutId: null,
		}
	}

	onClickProfileMouseEnter = () => {
		this.setState({ showProfileMenu: true })
	}

	onClickProfileMouseLeave = () => {
		this.s
	}

	handleProfileMouseEnter = () => {
		if (this.state.timeoutId) {
			clearTimeout(this.state.timeoutId) // Отменяем таймер, если мышка входит
		}
		this.setState({ showProfileMenu: true })
	}

	handleProfileMouseLeave = () => {
		const timeoutId = setTimeout(() => {
			this.setState({ showProfileMenu: false })
		}, 2000) // 2 секунды
		this.setState({ timeoutId })
	}

	handleMenuMouseEnter = () => {
		if (this.state.timeoutId) {
			clearTimeout(this.state.timeoutId)
			this.setState({ showProfileMenu: true }) // Отменяем таймер при наведении на меню
		}
	}

	handleMenuMouseLeave = () => {
		const timeoutId = setTimeout(() => {
			this.setState({ showProfileMenu: false })
		}, 400) // 2 секунды
		this.setState({ timeoutId })
	}

	render() {
		return (
			<div
				className='profile'
				onMouseEnter={this.handleProfileMouseEnter}
				onMouseLeave={this.handleProfileMouseLeave}
				onClick={this.onClickProfileMouseEnter}
			>
				<a href='#'>
					<img src={icon} alt='Profile' />
				</a>
				<div
					className={`profile-menu ${this.state.showProfileMenu ? 'show' : ''}`}
					onMouseEnter={this.handleMenuMouseEnter}
					onMouseLeave={this.handleMenuMouseLeave}
				>
					<ul>
						<li>
							<a href='#'>Sign In</a>
						</li>
						<li>
							<a href='#'>Sign Up</a>
						</li>
					</ul>
				</div>
			</div>
		)
	}
}

export default Icon
